mapper.select:
select *
from dataset_info
where del_flag = false

    and is_public = #{request.isPublic}


    and ( name like CONCAT(CONCAT('%', #{request.query}), '%')


    or introduction like CONCAT(CONCAT('%', #{request.query}), '%') )


    and task_type = #{request.taskType}


    and

        find_in_set(#{tag}, tags)



order by ${request.orderByField} ${request.orderByType}
