mapper.sql:
t.del_flag = 0

    AND (t.task_name LIKE CONCAT('%', #{query.q}, '%') OR t.task_code LIKE CONCAT('%', #{query.q}, '%'))


    AND t.status = #{query.status}


    AND t.template_id = #{query.templateId}


